import React, { useState, useEffect } from "react";
import "./App.css"; // This connects to the CSS you just shared
import { Gamepad2, Trophy, Star, Zap, Sparkles, Menu } from 'lucide-react'; 
import { Routes, Route, useNavigate } from "react-router-dom";

// --- Components (Placeholders for now, we will update these as you send screenshots) ---
import Login from "./Login";
import Signup from "./Signup";
import Dashboard from "./components/Dashboard"; 

export default function App() {
  const [view, setView] = useState("home"); // Controls what page is shown
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  // Check if user is already logged in
  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  return (
    <div className="app-root">
      
      {/* ================================================================== */}
      {/* HOME PAGE UI (Matches your CSS exactly)                           */}
      {/* ================================================================== */}
      {view === "home" && (
        <div className="landing-page">
          
          {/* 1. Navbar */}
          <nav className="navbar">
            <div className="logo">
              <Gamepad2 size={32} strokeWidth={2.5} />
              <span>VidyaQuest</span>
            </div>
            
            <div className="nav-links">
              <span className="active">Home</span>
              <span>Subjects</span>
              <span>Games</span>
              <span>Leaderboard</span>
            </div>

            <div className="auth-buttons">
              {!isLoggedIn ? (
                <>
                  <button className="btn-nav-login" onClick={() => navigate("/login")}>Sign In</button>
                  <button className="btn-nav-signup" onClick={() => navigate("/signup")}>Sign Up</button>
                </>
              ) : (
                <button className="btn-nav-signup" onClick={() => setView("dashboard")}>Go to Dashboard</button>
              )}
            </div>
          </nav>

          {/* 2. Hero Section (The Main Center Part) */}
          <div className="hero-container">
            
            {/* Animated Floating Icons (These bounce up and down) */}
            <div className="floating-icon star-icon">
              <Star size={28} fill="currentColor" />
            </div>
            <div className="floating-icon trophy-icon">
              <Trophy size={28} fill="currentColor" />
            </div>
            <div className="floating-icon bolt-icon">
               <Zap size={28} fill="currentColor" />
            </div>

            {/* Main Text */}
            <span className="badge">âœ¨ For Rural Schools (Grades 6-12)</span>
            
            <h1 className="hero-title">
              Gamified Learning <br />
              <span className="highlight">For Future Innovators!</span>
            </h1>
            
            <p className="hero-subtitle">
              Master Science and Math through interactive games. <br />
              Works offline. No internet? No problem.
            </p>
            
            {/* Big Action Buttons */}
            <div className="hero-buttons">
              <button className="btn-primary-lg" onClick={() => isLoggedIn ? setView("dashboard") : navigate("/login")}>
                ðŸš€ Start Adventure
              </button>
              <button className="btn-secondary-lg">
                â–¶ Watch Demo
              </button>
            </div>

            {/* 3. Stats Row (Bottom Cards) */}
            <div className="stats-row">
              <div className="stat-card">
                 <span className="stat-num">10K+</span>
                 <span className="stat-label">Happy Students</span>
              </div>
              <div className="stat-card">
                 <span className="stat-num">500+</span>
                 <span className="stat-label">STEM Lessons</span>
              </div>
              <div className="stat-card">
                 <div className="floating-icon sparkle-icon" style={{position: 'relative', top: '-10px', left: '0', animation: 'none'}}>
                    <Sparkles size={24} />
                 </div>
                 <span className="stat-num">100%</span>
                 <span className="stat-label">Free Access</span>
              </div>
            </div>

          </div>
        </div>
      )}

      {/* ================================================================== */}
      {/* OTHER PAGES (Dashboard, Login, etc.)                              */}
      {/* ================================================================== */}
      
      {view === "dashboard" && (
         <Dashboard />
      )}

      {/* Routes for Login/Signup */}
      <Routes>
        <Route path="/login" element={<Login onSuccess={() => { setIsLoggedIn(true); setView("dashboard"); }} />} />
        <Route path="/signup" element={<Signup onSuccess={() => navigate("/login")} />} />
      </Routes>

    </div>
  );
}
