import React, { useState } from "react";
import "./App.css";
// Icons
import { 
  Gamepad2, Trophy, Star, Zap, Sparkles, 
  WifiOff, Languages, Bot, Calculator, Atom, 
  Monitor, Globe, BookOpen, Palette, Lock, Dumbbell,
  Facebook, Twitter, Instagram, Youtube, Mail, Phone, MapPin,
  X, MessageCircle, ChevronDown
} from 'lucide-react';
import { Routes, Route, useNavigate } from "react-router-dom";

// Components
import Login from "./Login";
import Signup from "./Signup";
import Dashboard from "./components/Dashboard";
import Chatbot from "./components/Chatbot"; 

export default function App() {
  const [view, setView] = useState("home");
  const [showChat, setShowChat] = useState(false);
  
  // --- NEW: Language State ---
  const [language, setLanguage] = useState("English");
  const [showLangMenu, setShowLangMenu] = useState(false);
  const languages = ["English", "Hindi", "Telugu"];

  const navigate = useNavigate();

  return (
    <div className="app-root">
      
      {/* ==================== HOME PAGE ==================== */}
      {view === "home" && (
        <>
          {/* 1. Navbar */}
          <nav className="navbar">
            <div className="logo"><Gamepad2 size={32} /> VidyaQuest</div>
            
            <div className="nav-links">
              <span>Home</span><span>Subjects</span><span>Games</span><span>Leaderboard</span>
            </div>

            <div className="auth-buttons">
              {/* --- NEW: Language Selector Button --- */}
              <div style={{position: 'relative'}}>
                <button 
                  className="btn-lang" 
                  onClick={() => setShowLangMenu(!showLangMenu)}
                >
                  <Globe size={18} /> {language} <ChevronDown size={14} />
                </button>
                
                {/* Dropdown Menu */}
                {showLangMenu && (
                  <div className="lang-dropdown">
                    {languages.map(lang => (
                      <div 
                        key={lang} 
                        className="lang-option"
                        onClick={() => { setLanguage(lang); setShowLangMenu(false); }}
                      >
                        {lang}
                      </div>
                    ))}
                  </div>
                )}
              </div>

              {/* Sign In / Sign Up */}
              <span style={{fontWeight:'600', cursor:'pointer'}} onClick={() => navigate("/login")}>Sign In</span>
              <button className="btn-primary" onClick={() => navigate("/signup")}>Sign Up</button>
            </div>
          </nav>

          {/* 2. Hero Section */}
          <header className="hero-section">
            <span className="grade-badge">✨ For Grades 6-12</span>
            <h1 className="hero-title">Learn, Play & <br /><span style={{color:'#3b82f6'}}>Grow!</span></h1>
            <p style={{fontSize:'1.2rem', color:'#64748b', marginBottom:'3rem'}}>Making STEM education fun and accessible for rural students</p>
            <div style={{display:'flex', justifyContent:'center', gap:'1rem'}}>
              <button className="btn-primary" onClick={() => navigate("/signup")}>✨ Start Learning</button>
              <button className="btn-secondary">▷ Watch Demo</button>
            </div>
            
            <div className="stats-row">
              <div className="stat-card"><span className="stat-num">10K+</span>Students Learning</div>
              <div className="stat-card"><span className="stat-num">500+</span>Lessons Completed</div>
              <div className="stat-card"><span className="stat-num">1000+</span>Hours of Content</div>
            </div>
          </header>

          {/* 3. Why Choose Us */}
          <section className="section-container">
            <h2 className="section-header">Why Choose Us?</h2>
            <div className="features-grid">
              <FeatureCard icon={<WifiOff />} color="bg-teal" title="Works Offline" text="Learn without internet - download lessons and study anywhere." />
              <FeatureCard icon={<Gamepad2 />} color="bg-orange" title="Fun Games & Quizzes" text="Earn XP, badges and compete with friends while learning." />
              <FeatureCard icon={<Languages />} color="bg-blue" title="Multiple Languages" text="Learn in English, Hindi, Telugu and more regional languages." />
              <FeatureCard icon={<Bot />} color="bg-pink" title="AI Tutor" text="Get instant help from our smart chatbot anytime." />
            </div>
          </section>

          {/* 4. Explore Subjects */}
          <section className="section-container" style={{background:'#f8fafc'}}>
            <h2 className="section-header">Explore Subjects</h2>
            <div className="subjects-grid">
              <SubjectCard title="Mathematics" lessons="45 Lessons" icon={<Calculator/>} color="grad-math" xp="1,200" progress={65} stars={5} />
              <SubjectCard title="Science" lessons="52 Lessons" icon={<Atom/>} color="grad-science" xp="980" progress={42} stars={4} />
              <SubjectCard title="Technology" lessons="38 Lessons" icon={<Monitor/>} color="grad-tech" xp="650" progress={28} stars={3} />
              
              {/* Locked Engineering Card */}
              <div className="subject-card card-locked">
                <div className="blur-layer"></div>
                <div className="lock-content">
                  <Lock size={32} style={{marginBottom:'10px'}}/>
                  <div>Complete Science to unlock</div>
                </div>
              </div>

              <SubjectCard title="Social Studies" lessons="48 Lessons" icon={<Globe/>} color="grad-social" xp="720" progress={35} stars={3} />
              <SubjectCard title="English" lessons="55 Lessons" icon={<Languages/>} color="grad-english" xp="850" progress={50} stars={4} btnColor="#db2777" />
              <SubjectCard title="Hindi" lessons="50 Lessons" icon={<BookOpen/>} color="grad-hindi" xp="780" progress={40} stars={4} />
              <SubjectCard title="Arts" lessons="35 Lessons" icon={<Palette/>} color="grad-arts" xp="500" progress={20} stars={2} />
              <SubjectCard title="Physical Education" lessons="30 Lessons" icon={<Dumbbell/>} color="grad-pe" xp="450" progress={25} stars={2} />
            </div>
          </section>

          {/* 5. CTA Banner */}
          <section className="cta-section">
             <div style={{background:'rgba(255,255,255,0.2)', display:'inline-block', padding:'5px 15px', borderRadius:'20px', marginBottom:'1rem'}}>✨ 100% Free to Start</div>
             <h2 style={{fontSize:'3rem', margin:'10px 0'}}>Ready to Start Your <br/> Learning Journey?</h2>
             <p style={{opacity:0.9, fontSize:'1.1rem'}}>Join thousands of students already learning with us!</p>
             <button className="btn-white" onClick={() => navigate("/signup")}>Join Now - Free! →</button>
          </section>

          {/* 6. Footer */}
          <footer className="footer">
             <div className="footer-grid">
                <div>
                   <div className="logo" style={{color:'white', marginBottom:'1.5rem'}}><Gamepad2 /> VidyaQuest</div>
                   <p style={{lineHeight:'1.6', opacity:0.8}}>Making quality STEM education accessible and fun for every rural student across India.</p>
                   <div className="socials">
                      <div className="social-icon"><Facebook size={18}/></div>
                      <div className="social-icon"><Twitter size={18}/></div>
                      <div className="social-icon"><Instagram size={18}/></div>
                      <div className="social-icon"><Youtube size={18}/></div>
                   </div>
                </div>
                <div><h4>Quick Links</h4><ul><li>Subjects</li><li>Games</li><li>Leaderboard</li><li>About Us</li></ul></div>
                <div><h4>Support</h4><ul><li>Help Center</li><li>Contact Us</li><li>Privacy Policy</li><li>Terms of Service</li></ul></div>
                <div>
                  <h4>Contact Us</h4>
                  <ul style={{opacity:0.9}}>
                    <li style={{display:'flex', gap:'10px', alignItems:'center'}}><Mail size={16}/> contact@vidyaquest.edu</li>
                    <li style={{display:'flex', gap:'10px', alignItems:'center'}}><Phone size={16}/> +91 1234 567 890</li>
                    <li style={{display:'flex', gap:'10px', alignItems:'center'}}><MapPin size={16}/> Govt of Odisha</li>
                  </ul>
                </div>
             </div>
             <div className="copyright">© 2025 VidyaQuest. All rights reserved. | Problem ID: 25048</div>
          </footer>
        </>
      )}

      {/* --- Chatbot Floating Logic (UNCHANGED) --- */}
      <div className="chatbot-float-btn" onClick={() => setShowChat(!showChat)}>
        {showChat ? <X size={30} /> : <MessageCircle size={30} />}
      </div>
      {showChat && (
        <div className="chatbot-window">
          <div className="chatbot-header"><span>Vidya AI Tutor</span><X style={{cursor:'pointer'}} onClick={() => setShowChat(false)}/></div>
          <div className="chatbot-content"><Chatbot /></div>
        </div>
      )}

      {/* --- Other Pages --- */}
      {view === "dashboard" && <Dashboard />}
      <Routes>
        <Route path="/login" element={<Login onSuccess={() => setView("dashboard")} />} />
        <Route path="/signup" element={<Signup onSuccess={() => navigate("/login")} />} />
      </Routes>
    </div>
  );
}

// Helper Components
function FeatureCard({ icon, color, title, text }) {
  return (
    <div className="feature-card">
      <div className={`icon-box ${color}`}>{icon}</div>
      <h3 style={{fontSize:'1.2rem', margin:'0 0 10px'}}>{title}</h3>
      <p style={{color:'#64748b', lineHeight:'1.5'}}>{text}</p>
    </div>
  );
}

function SubjectCard({ title, lessons, icon, color, xp, progress, stars, btnColor }) {
  return (
    <div className={`subject-card ${color}`}>
      <div>
        <div className="subject-header">
           <div className="sub-icon">{icon}</div>
           <div className="star-tag"><Star size={12} fill="gold"/> {stars}</div>
        </div>
        <div style={{marginTop:'1.5rem'}}>
           <h3 style={{fontSize:'1.4rem', margin:'0 0 5px'}}>{title}</h3>
           <p style={{margin:0, opacity:0.9, fontSize:'0.9rem'}}>{lessons}</p>
        </div>
      </div>
      <div>
         <div style={{display:'flex', justifyContent:'space-between', fontSize:'0.8rem'}}>
           <span>Progress</span><span>{progress}%</span>
         </div>
         <div className="progress-bar"><div className="progress-fill" style={{width: `${progress}%`}}></div></div>
         <div style={{fontSize:'0.9rem', fontWeight:'bold', marginBottom:'10px'}}>XP {xp}</div>
         <button className="btn-continue" style={btnColor ? {background: btnColor, color: 'white'} : {}}>Continue Learning {'>'}</button>
      </div>
    </div>
  );
}
