import React, { useState, useEffect } from "react";
import "./App.css";
// Importing Components
import Dashboard from "./components/Dashboard";
import Lesson from "./components/Lesson";
import Quiz from "./components/Quiz";
import Chatbot from "./components/Chatbot";
import Login from "./Login";
import Signup from "./Signup";
import Profile from "./components/Profile";
// Importing Icons for the new UI
import { Gamepad2, Trophy, Star, Zap, Sparkles } from 'lucide-react'; 
import { Routes, Route, useNavigate } from "react-router-dom";

// Keep your existing Lesson Data
const STEM_LESSONS = [
  {
    id: 1,
    title: "Bright Bulbs: Simple Circuits",
    subject: "Science üí° Electricity",
    content: "Learn how electric current flows in a simple circuit with a bulb and a battery.",
    quiz: [
      { q: "Does electric current need a closed loop to flow?", a: "yes" },
      { q: "Is the battery the source of energy in this circuit?", a: "yes" }
    ]
  },
  {
    id: 2,
    title: "Fractions in Daily Life",
    subject: "Mathematics üî¢ Fractions",
    content: "Understand halves, thirds and quarters using food and classroom examples.",
    quiz: [
      { q: "Is 1/2 bigger than 1/3?", a: "yes" },
      { q: "Do two quarters make a half?", a: "yes" }
    ]
  },
  {
    id: 3,
    title: "First Steps in Coding",
    subject: "Technology üíª Programming",
    content: "Discover what algorithms are and how to give clear instructions to a computer.",
    quiz: [
      { q: "Is an algorithm a set of steps?", a: "yes" },
      { q: "Can a computer understand vague instructions?", a: "no" }
    ]
  },
  {
    id: 4,
    title: "Build a Strong Bridge",
    subject: "Engineering üåâ Structures",
    content: "Explore how shapes like triangles make bridges strong and safe.",
    quiz: [
      { q: "Do triangles help make bridges stronger?", a: "yes" },
      { q: "Is a bridge only used for cars?", a: "no" }
    ]
  }
];

export default function App() {
  const [view, setView] = useState("home"); // 'home', 'dashboard', 'lesson', 'quiz', 'chat'
  const [currentLesson, setCurrentLesson] = useState(null);
  const [lessons, setLessons] = useState(STEM_LESSONS);
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem('token'));
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  // Helper to handle "Start Learning"
  const handleStartLearning = () => {
    if (isLoggedIn) {
      setView("dashboard");
    } else {
      navigate("/login");
    }
  };

  return (
    <div className="app-root">
      
      {/* -------------------------------------------------- */}
      {/* NEW UI: LANDING PAGE (Only shows when view is 'home') */}
      {/* -------------------------------------------------- */}
      {view === "home" && (
        <div className="landing-page">
          {/* 1. Navbar */}
          <nav className="navbar">
            <div className="logo">
              <Gamepad2 size={32} strokeWidth={2.5} />
              <span>VidyaQuest</span>
            </div>
            <div className="nav-links">
              <span onClick={() => setView("home")}>Home</span>
              <span>Subjects</span>
              <span>Games</span>
              <span>Leaderboard</span>
            </div>
            <div className="auth-buttons">
              {!isLoggedIn ? (
                <>
                  <button className="btn-nav-login" onClick={() => navigate("/login")}>Sign In</button>
                  <button className="btn-nav-signup" onClick={() => navigate("/signup")}>Sign Up</button>
                </>
              ) : (
                <button className="btn-nav-signup" onClick={() => setView("dashboard")}>Dashboard</button>
              )}
            </div>
          </nav>

          {/* 2. Hero Section */}
          <div className="hero-container">
            {/* Floating Icons */}
            <div className="floating-icon star-icon"><Star size={28} fill="currentColor" /></div>
            <div className="floating-icon trophy-icon"><Trophy size={28} fill="currentColor" /></div>
            
            <span className="badge">‚ú® For Grades 6-12</span>
            
            <h1 className="hero-title">
              Learn, Play & <br />
              <span className="highlight">Grow!</span>
            </h1>
            
            <p className="hero-subtitle">
              Making STEM education fun and accessible for rural students <br />
              with offline-ready gamified lessons.
            </p>
            
            <div className="hero-buttons">
              <button className="btn-primary-lg" onClick={handleStartLearning}>
                ‚ú® Start Learning
              </button>
              <button className="btn-secondary-lg">
                ‚ñ∂ Watch Demo
              </button>
            </div>

            {/* 3. Stats Cards */}
            <div className="stats-row">
              <div className="stat-card">
                 <div className="floating-icon bolt-icon"><Zap size={24} fill="currentColor"/></div>
                 <span className="stat-num">10K+</span>
                 <span className="stat-label">Students Learning</span>
              </div>
              <div className="stat-card">
                 <span className="stat-num">500+</span>
                 <span className="stat-label">Lessons Completed</span>
              </div>
              <div className="stat-card">
                 <div className="floating-icon sparkle-icon"><Sparkles size={24} /></div>
                 <span className="stat-num">1000+</span>
                 <span className="stat-label">Hours of Content</span>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* -------------------------------------------------- */}
      {/* EXISTING LOGIC: DASHBOARD & LESSONS                */}
      {/* -------------------------------------------------- */}

      {view === "dashboard" && (
        <div className="page-container">
           {/* Simple Header for Dashboard */}
           <header className="navbar">
              <div className="logo"><Gamepad2 /> VidyaQuest</div>
              <div className="nav-links">
                 <button className="btn-nav-login" onClick={() => setView("home")}>Home</button>
                 <button className="btn-nav-login" onClick={() => setView("chat")}>AI Chatbot</button>
              </div>
           </header>
           
           <Dashboard 
             lessons={lessons} 
             onOpen={(l) => { setCurrentLesson(l); setView("lesson"); }} 
           />
        </div>
      )}

      {view === "lesson" && currentLesson && (
        <Lesson 
          lesson={currentLesson} 
          onBack={() => setView("dashboard")} 
          onQuiz={() => setView("quiz")} 
        />
      )}

      {view === "quiz" && currentLesson && (
        <Quiz 
          lesson={currentLesson} 
          questions={currentLesson.quiz} 
          onBack={() => setView("lesson")} 
        />
      )}

      {view === "chat" && (
         <div style={{ padding: '2rem' }}>
            <button className="btn-secondary-lg" onClick={() => setView("dashboard")} style={{marginBottom:'1rem'}}>‚Üê Back to Dashboard</button>
            <Chatbot />
         </div>
      )}

      {/* Auth Routes */}
      <Routes>
        <Route path="/login" element={<Login onSuccess={() => { setIsLoggedIn(true); setView("dashboard"); }} />} />
        <Route path="/signup" element={<Signup onSuccess={() => navigate("/login")} />} />
        <Route path="/profile" element={<Profile />} />
      </Routes>

    </div>
  );
}
